  //   )
  //     .then(response => {
  //       console.log(response);
  //     })
  //     .catch(error => {
  //       console.log(error);
  //     });

  // if (formValid(this.state)) {
  //   console.log(`
  //     --SUBMITTING--
  //     First Name: ${this.state.firstName}
  //     Last Name: ${this.state.lastName}
  //     Username: ${this.state.userName}
  //     Email: ${this.state.email}
  //     Password: ${this.state.password}
  //   `);
  // } else {
  //   console.error("FORM INVALID - DISPLAY ERROR MESSAGE");
  // }
  // };

  handleChange = e => {
    e.preventDefault();
    const { name, value } = e.target;
    let formErrors = { ...this.state.formErrors };

    switch (name) {
      case "firstName":
        formErrors.firstName =
          value.length < 2 ? "minimum 2 characters required" : "";
        break;
      case "lastName":
        formErrors.lastName =
          value.length < 2 ? "minimum 2 characters required" : "";
        break;
      case "userName":
        formErrors.userName =
          value.length < 2 ? "minimum 2 characters required" : "";
        break;
      case "email":
        formErrors.email = emailRegex.test(value)
          ? ""
          : "invalid email address";
        break;
      case "password":
        formErrors.password =
          value.length < 6 ? "minimum 6 characters required" : "";
        break;
      default:
        break;
    }

    this.setState({ formErrors, [name]: value }, () => console.log(this.state));
  };
  state = {
    selectedFile: null
  };
  fileSelectedHandler = event => {
    console.log(event.target.files[0]); //LOGS THE SELECTED FILE

    this.setState({
      //SELECTS THE FILE
      selectedFile: event.target.files[0]
    });
  };
  fileUploadHandler = () => {
    //UPLOADING THE SELECTED FILE USING FORM DATA
    const fd = new FormData();
    fd.append("image", this.state.selectedFile, this.state.selectedFile.name);
    Axios.post("", fd, {
      //LOCATION TO POST TO (place location between quotes)
      onUploadProgress: progressEvent => {
        console.log(
          "Image Upload Progress: " +
            Math.round((progressEvent.loaded / progressEvent.total) * 100) +
            "%"
        );
      }
    }).then(res => {
      console.log(res);
    });
  };
  //FORM DATA
  render() {
    const { formErrors } = this.state;

    return (
      <div className="wrapper">
        <div className="form-wrapper">
          <h1>Create an Account</h1>
          <form
            className="account-form"
            onSubmit={this.handleSubmit}
            noValidate
            method="POST"
            action="https://b0sq8n7q17.execute-api.us-east-1.amazonaws.com/createAccount"
          >
            <div className="firstName">
              <label className="label" htmlFor="firstName"></label>
              <input
                className={formErrors.firstName.length > 0 ? "error" : null}
                placeholder="First Name"
                type="text"
                name="firstName"
                noValidate
                onChange={this.handleChange}
              />
              {formErrors.firstName.length > 0 && (
                <span className="errorMessage">{formErrors.firstName}</span>
              )}
            </div>
            <div className="lastName">
